import React from "react";
import {
  Box,
  Container,
  Typography,
  Button,
  TextField,
  Grid,
  CssBaseline,
  Avatar,
} from "@mui/material";

const Signup = () => {
  return (
    <Container component="main" maxWidth="xs">
      <CssBaseline />
      <Box
        sx={{
          marginTop: 8,
          display: "flex",
          flexDirection: "column",
          alignItems: "center",
        }}
      ></Box>
      <Box style={{ paddingLeft: 160 }}>
        <Avatar sx={{ m: 1, bgcolor: "secondary.main" }}>
          <LockOutlinedIcon />
        </Avatar>
      </Box>
      <Box style={{ paddingLeft: 145 }}>
        <Typography variant="h5">Sign up</Typography>
      </Box>

      <Box component="form" sx={{ mt: 3 }} onSubmit={handleSubmit}>
        <Grid container spacing={2}>
          <Grid item xs={12}>
            <TextField
              required
              fullWidth
              name="name"
              onKeyDown={(event) => {
                const isValidKey = /[a-zA-Z0-9]/i.test(event.key);
                if (!isValidKey) {
                  event.preventDefault();
                }
              }}
              label="Enter your Username"
              value={signInData.name}
              onChange={handleChange}
            />
          </Grid>
          <Grid item xs={12}>
            <TextField
              required
              fullWidth
              name="password"
              label="Password"
              type="password"
              value={signInData.password}
              onChange={handleChange}
              error={signInData.passwordMatchError}
            />
          </Grid>
          <Grid item xs={12}>
            <TextField
              required
              fullWidth
              name="confirmpassword"
              label="Confirm Password"
              type="password"
              value={signInData.confirmpassword}
              onChange={handleChange}
              error={signInData.passwordMatchError}
            />
          </Grid>
          <FormHelperText style={{ paddingLeft: 50 }} sx={{ mt: 2 }}>
            {formHelper}
          </FormHelperText>
          <Button
            type="submit"
            fullWidth
            variant="contained"
            sx={{ mt: 3, mb: 2 }}
          >
            Sign Up
          </Button>
          <Grid container justifyContent="flex-end">
            <Grid item>
              <Typography variant="body2" underline="none">
                <Link to="/">Already have an account? Log in</Link>
              </Typography>
            </Grid>
          </Grid>
        </Grid>
      </Box>
    </Container>
  );
};

export default Signup;
