import React from "react";
import { Box, Typography } from "@mui/material";
import DeleteSharpIcon from "@mui/icons-material/DeleteSharp";

const Users = () => {
  return (
    <Box
      bgcolor="#36454F"
      padding="20px"
      borderRadius={5}
      display="flex"
      alignItems="center"
      flexWrap="wrap"
    >
      <Box flexGrow={1}>
        <Typography
          color="white"
          fontWeight="bold"
          paddingBottom="10px"
          //style={strikeThrough}
        >
          User 1
        </Typography>
        {show && (
          <Typography color="white" gutterBottom>
            {description}
          </Typography>
        )}
      </Box>

      <Box marginLeft="auto" onClick={deletetask}>
        <DeleteSharpIcon style={{ color: "white" }} />
      </Box>

      {done ? (
        <Box marginLeft="auto" onClick={handleDone}>
          <CheckBoxSharpIcon style={{ color: "white" }} />
        </Box>
      ) : (
        <Box marginLeft="auto" onClick={handleDone}>
          <CheckBoxOutlineBlankSharpIcon style={{ color: "white" }} />
        </Box>
      )}

      {show ? (
        <Box marginLeft="auto" onClick={handleClick}>
          <KeyboardArrowUpSharpIcon style={{ color: "white" }} />
        </Box>
      ) : (
        <Box marginLeft="auto" onClick={handleClick}>
          <KeyboardArrowDownIcon style={{ color: "white" }} />
        </Box>
      )}
    </Box>
  );
};

export default Users;
